"use strict";(self.webpackChunknblocks_docs=self.webpackChunknblocks_docs||[]).push([[9267],{8366:e=>{e.exports=JSON.parse('{"label":"security","permalink":"/nblocks-docs/docs/tags/security","allTagsPath":"/nblocks-docs/docs/tags","count":1,"items":[{"id":"authentication-and-access/roles-and-privileges","title":"Roles & Privileges","description":"In Nblocks, each user is assigned a role that grants them specific privileges. Roles act as groups for these privileges, enabling fine-grained control over what each user can see and do within your application. This setup is key to crafting a secure and robust system. While the OWNER role and AUTHENTICATED privilege are mandatory and cannot be changed, you have full flexibility to define additional roles and privileges based on your needs.","permalink":"/nblocks-docs/docs/authentication-and-access/roles-and-privileges"}]}')}}]);