"use strict";(self.webpackChunknblocks_docs=self.webpackChunknblocks_docs||[]).push([[6290],{929:t=>{t.exports=JSON.parse('{"label":"guide","permalink":"/nblocks-docs/docs/tags/guide","allTagsPath":"/nblocks-docs/docs/tags","count":6,"items":[{"id":"authentication-and-access/protect-backend","title":"Protect your backend","description":"Securing your backend is even more important than securing your frontend since the backend usually holds sensitive and important information that we never want to return to a user that is either not logged in or not the rightful recipient of that information.","permalink":"/nblocks-docs/docs/authentication-and-access/protect-backend"},{"id":"authentication-and-access/protect-frontend","title":"Protect your frontend","description":"Securing your frontent when you have logged in users is crucial.","permalink":"/nblocks-docs/docs/authentication-and-access/protect-frontend"},{"id":"user-management/quickstart-branding","title":"Quickstart Branding","description":"In this quickstart we\'ll apply branding to the Nblocks hosted views like Nblock Login and User management portal.","permalink":"/nblocks-docs/docs/user-management/quickstart-branding"},{"id":"authentication-and-access/quickstart-user-login","title":"Quickstart User Login","description":"On this page you will integrate your web application with Nblocks hosted login and have your users authenticate in no time. You will apply a few lines of code to make redirects using the familiar OpenId Connect and OAuth 2.0 flows.","permalink":"/nblocks-docs/docs/authentication-and-access/quickstart-user-login"},{"id":"authentication-and-access/quickstart-user-ready","title":"Quickstart User Ready","description":"On this page you will integrate your web application with Nblocks hosted login, add protection to your app and have your users authenticate to get access. We call this beeing user ready and you will accomplish this in no time.","permalink":"/nblocks-docs/docs/authentication-and-access/quickstart-user-ready"},{"id":"getting-started/signup","title":"Signup for Nblocks","description":"This page is in progress.","permalink":"/nblocks-docs/docs/getting-started/signup"}]}')}}]);